pragma solidity >= 0.4.22;

import "./ownable.sol";

contract ReportFactory is Ownable{
    
    enum Status{
        Infected,
        Recovered,
        Dead
    }
    
    struct Report{
        address reporter;
        
        uint reportId;
        uint localPatientId;
        /*Initital report about the patient (Location, Hospital, Symptoms, On set of symptoms, Test date, Physician, Physician Email, 
            Possible physical contacts)*/
        string firstReport_hash;
        string location; //Country
        string[] records_hash; //Various medical entries
        
        Status status;
    }
    
    Report[] public reports;
    
    mapping(address => bool) reporterToVerified;
    mapping(uint => address) reportToReporter;
    mapping(address => uint[]) public reporterToReports;
    mapping(uint => uint) public statusToCount;
    mapping(string => uint[]) public countryToReports;
    
    modifier isVerified(address _reporter){
        require(reporterToVerified[_reporter] == true, "Not Permitted!");
        _;
    }
    
    function createNewReport(uint _localPatientId, uint8 _status, string calldata _location, string calldata _firstReport_hash) external isVerified(msg.sender) returns(uint){
        
        Report  memory report;
        report.localPatientId = _localPatientId;
        report.reporter = msg.sender;
        report.firstReport_hash = _firstReport_hash;
        report.status = Status(_status);
        report.location = _location;
        
        statusToCount[_status]++;
        uint id = reports.push(report) - 1;
        
        reports[id].reportId = id;
        
        reportToReporter[id] = msg.sender;
        reporterToReports[msg.sender].push(id);
        countryToReports[_location].push(id);
        
        return id;
    }
    
    function createNewRecord(uint _reportId, string calldata  _record_hash) external{
        require(msg.sender == reportToReporter[_reportId]);
        
        Report storage report = reports[_reportId];
        report.records_hash.push(_record_hash);
    }
}
